import React, { useEffect, useState } from 'react';

function EventFeed() {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(true);

    // Get logged-in user from localStorage
    const loggedInUser = JSON.parse(localStorage.getItem("user"));

    useEffect(() => {
        const token = localStorage.getItem("token");

        fetch('http://localhost:5000/api/events', {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json'
            }
        })
        .then((res) => res.json())
        .then((data) => {
            setEvents(data);
            setLoading(false);
        })
        .catch((err) => {
            console.error('Error fetching events:', err);
            setLoading(false);
        });
    }, []);

    const handleRSVP = async (eventId) => {
        try {
            const token = localStorage.getItem("token");

            const response = await fetch(`http://localhost:5000/api/events/${eventId}/rsvp`, {
                method: 'POST',
                headers: { 
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ employeeName: loggedInUser.name })
            });

            if (response.ok) {
                alert('RSVP successful!');
                setEvents(events.map(event =>
                    event._id === eventId
                        ? { ...event, availableSpots: event.availableSpots - 1 }
                        : event
                ));
            } else {
                const result = await response.json();
                alert(result.message);
            }
        } catch (error) {
            console.error('Failed to RSVP:', error);
        }
    };

    if (loading) return <p>Loading events...</p>;

    const filteredEvents = events.filter(event => 
        event.eventType === "firm-wide" || 
        event.eventType === "limited-entry" || 
        (event.eventType === "team-specific" && event.team === loggedInUser.team) || 
        !event.team // Show events that don‚Äôt have a team (public events)
    );

    return (
        <div style={styles.container}>
            <h2 style={styles.header}>üìÖ Event Feed</h2>
            {filteredEvents.length === 0 ? (
                <p>No upcoming events.</p>
            ) : (
                filteredEvents.map((event) => (
                    <div key={event._id} style={styles.card}>
                        <h3 style={styles.title}>{event.title}</h3>
                        <p>{event.description}</p>
                        <p><strong>Date:</strong> {new Date(event.date).toLocaleDateString()}</p>
                        <p><strong>Type:</strong> {event.eventType}</p>

                        {event.eventType === 'limited-entry' && (
                            <p style={styles.seats}>üéüÔ∏è Available Seats: {event.availableSpots}</p>
                        )}

                        {event.eventType === 'limited-entry' && event.availableSpots > 0 && (
                            <button style={styles.rsvpButton} onClick={() => handleRSVP(event._id)}>
                                RSVP
                            </button>
                        )}
                        {event.eventType === 'limited-entry' && event.availableSpots === 0 && (
                            <button style={styles.rsvpButton} disabled>
                                Event Full
                            </button>
                        )}
                    </div>
                ))
            )}
        </div>
    );
}

const styles = {
    container: {
        padding: "20px",
        maxWidth: "800px",
        margin: "auto",
        backgroundColor: "#f3f8f5",
        borderRadius: "10px"
    },
    header: {
        textAlign: "center",
        fontSize: "24px",
        marginBottom: "20px",
    },
    card: {
        backgroundColor: "white",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
        marginBottom: "15px"
    },
    title: {
        color: "#007bff",
        marginBottom: "5px",
    },
    seats: {
        color: "red",
        fontWeight: "bold",
    },
    rsvpButton: {
        backgroundColor: "#28a745",
        color: "white",
        border: "none",
        padding: "10px",
        cursor: "pointer",
        borderRadius: "5px",
        marginTop: "10px",
    }
};

export default EventFeed;
