import React, { useEffect, useState } from "react";
import axios from "axios";

const EventFeed = () => {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/api/events")
            .then((res) => {
                setEvents(res.data);
            })
            .catch((err) => console.error("Error fetching events:", err));
    }, []);

    return (
        <div style={styles.container}>
            <h2 style={styles.header}>
                üìÖ Event Feed
            </h2>
            {events
                .filter(event => !event.team || event.team === "All")  // Show events with no team or "All"
                .map((event) => (
                    <div key={event._id} style={styles.card}>
                        <h3 style={styles.title}>{event.name}</h3>
                        <p>{event.description}</p>
                        <p><strong>Date:</strong> {event.date}</p>
                        <p><strong>Type:</strong> {event.type}</p>

                        {/* Show available seats & RSVP for limited-entry events */}
                        {event.type === "Limited-Entry" && (
                            <>
                                <p style={styles.seats}>üéüÔ∏è <strong>Available Seats:</strong> {event.availableSeats}</p>
                                <button style={styles.rsvpButton}>RSVP</button>
                            </>
                        )}
                    </div>
                ))
            }
        </div>
    );
};

const styles = {
    container: {
        padding: "20px",
        maxWidth: "800px",
        margin: "auto",
        backgroundColor: "#f3f8f5",
        borderRadius: "10px"
    },
    header: {
        textAlign: "center",
        fontSize: "24px",
        marginBottom: "20px",
    },
    card: {
        backgroundColor: "white",
        padding: "20px",
        borderRadius: "10px",
        boxShadow: "0 2px 5px rgba(0,0,0,0.1)",
        marginBottom: "15px"
    },
    title: {
        color: "#007bff",
        marginBottom: "5px",
    },
    seats: {
        color: "red",
        fontWeight: "bold",
    },
    rsvpButton: {
        backgroundColor: "#28a745",
        color: "white",
        border: "none",
        padding: "10px",
        cursor: "pointer",
        borderRadius: "5px",
        marginTop: "10px",
    }
};

export default EventFeed;
