import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./components/Login";
import EventFeed from "./components/EventFeed";
import "./App.css";

const App = () => {
    const [loggedInUser, setLoggedInUser] = useState(null);

    // Load the logged-in user from localStorage on component mount
    useEffect(() => {
        const storedUser = JSON.parse(localStorage.getItem("loggedInUser"));
        if (storedUser) {
            setLoggedInUser(storedUser);
        }
    }, []);

    return (
        <div className="App">
            <h1> Event Feed</h1>
            <Router>
                <Routes>
                    <Route 
                        path="/" 
                        element={loggedInUser ? <Navigate to="/feed" /> : <Login setLoggedInUser={setLoggedInUser} />} 
                    />
                    <Route 
                        path="/feed" 
                        element={loggedInUser ? <EventFeed loggedInUser={loggedInUser} /> : <Navigate to="/" />} 
                    />
                </Routes>
            </Router>
        </div>
    );
};

export default App;
