import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventFeed = () => {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const fetchEvents = async () => {
      const res = await axios.get('/api/events');
      setEvents(res.data);
    };
    fetchEvents();
  }, []);

  const handleRSVP = async (id) => {
    try {
      const res = await axios.post(`/api/events/rsvp/${id}`);
      setEvents((prevEvents) =>
        prevEvents.map((event) =>
          event._id === id ? { ...event, vacancies: event.vacancies - 1 } : event
        )
      );
    } catch (err) {
      alert('Error RSVPing');
    }
  };

  return (
    <div className="event-feed">
      <h2>Upcoming Events</h2>
      {events.map((event) => (
        <div key={event._id} className="event">
          <h3>{event.title}</h3>
          <p>{event.description}</p>
          <p>Team: {event.team}</p>
          <p>Vacancies: {event.vacancies}</p>
          {event.vacancies > 0 ? (
            <button onClick={() => handleRSVP(event._id)}>RSVP</button>
          ) : (
            <button disabled>No vacancies</button>
          )}
        </div>
      ))}
    </div>
  );
};

export default EventFeed;
